//https://medium.freecodecamp.org/how-to-make-create-react-app-work-with-a-node-backend-api-7c5c48acb1b0

import React from 'react';
import './App.css';
import logo from './logo.svg';

class App extends React.Component {
  state = {
    text: '',
    response: ''
  }

  _handleChange = (event) => {
    this.setState({ text: event.target.value })
  }

  _callApi = async () => {
    const response = await fetch('/api');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  _handleSubmit = (e) => {
    this._callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
    e.preventDefault();
  }

  render() {

    return (
      <div className="App">
        <header className="App-header">
          <img src='https://www.pinhype.com/wp-content/uploads/2017/03/PH85_8_Ball_Pin-595x595.png' className="App-logo" alt="logo" />
          <p>
            Ask the Magic 8 Ball
          </p>
        </header>
        <body className="App-body">
          <div className="think-and-ask">
            <button
              style={{borderRadius: '4px', backgroundColor: 'white', color: 'black'}}
            >
              Think and ask
            </button>
          </div>
          <div className="write-and-ask">
            <form onSubmit={this._handleSubmit}>
            <label>
            <textarea value={this.state.text} onChange={this._handleChange} />
            </label>
            <input type="submit" value="Submit" />
            </form>
          </div>

          <h1>{this.state.response}</h1>
        </body>

      </div>
    );
  }
}

export default App;
