//https://medium.freecodecamp.org/how-to-make-create-react-app-work-with-a-node-backend-api-7c5c48acb1b0

import React from 'react';
import './App.css';
import logo from './logo.svg';


const App = () => {
  return(
    <div className="App">
      <header className="App-header">
        <h1 style={{marginTop: '70px'}}>
          Just ask me
        </h1>
        <img src='https://www.pinhype.com/wp-content/uploads/2017/03/PH85_8_Ball_Pin-595x595.png' className="App-logo" alt="logo" />
      </header>
      <Body/>
    </div>
  )
}


class Body extends React.Component {

  state = {
    show_validation: false,
    text: '',
    response: ''
  }

  _handleChange = (event) => {
    this.setState({ text: event.target.value })
  }

  _callApi = async () => {
    const response = await fetch('/api');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };

  _handleSubmit = (e) => {
    if(this.state.text !== '') {
      this.setState({
        text: '',
        show_validation: false
      })
      this._callApi()
        .then(res => this.setState({ response: res.express }))
        .catch(err => console.log(err));
    }
    else {
      this.setState({ show_validation: true })
    }



    e.preventDefault();
  }


  render() {
    return(
      <body className="App-body">
        <div style={{borderColor: 'white'}}>
          <button
            style={{borderRadius: '4px', backgroundColor: 'white', color: 'black', fontSize: '24px'}}
            onClick={this._handleSubmit}
          >
            Think and ask
          </button>
        </div>
        <div style={{marginTop: '30px'}}>
          <h2 style={{color: 'white'}}>Write your question</h2>
          <form onSubmit={this._handleSubmit}>
          <label>
          <textarea value={this.state.text} onChange={this._handleChange} />
          </label>
          <input type="submit" value="Submit" />
          {this.state.text === '' && this.state.show_validation === true &&
            <small style={{color: '#ff4d4d'}}>come on..</small>
          }
          </form>
        </div>
        <h1 style={{color: 'white'}}>{this.state.response}</h1>
      </body>
    )
  }
}



export default App;
